", "			
", "			", list(), " ", list("married"), ", indicating marital status (1 if married, 0 otherwise);
", "			
", "			", list(), 
    " ", list("nodegree"), ", indicating high school diploma (1 if no degree, 0 otherwise);
", "			
", "			", list(), " ", list("re74"), ", real earnings in 1974;
